import React, { useState } from 'react';

const AdBanner = () => {
  // Sample ad data - you would replace this with your actual data
  const adData = [
    { id: 1, imageUrl: "data:image/png;base64,", companyName: "Company One", link: "https://example.com/1" },
    { id: 2, imageUrl: "data:image/png;base64,", companyName: "Company Two", link: "https://example.com/2" },
    { id: 3, imageUrl: "data:image/png;base64,", companyName: "Company Three", link: "https://example.com/3" },
    { id: 4, imageUrl: "data:image/png;base64,", companyName: "Company Four", link: "https://example.com/4" },
    { id: 5, imageUrl: "data:image/png;base64,", companyName: "Company Five", link: "https://example.com/5" },
    { id: 6, imageUrl: "data:image/jpeg;base64,", companyName: "Company Six", link: "https://example.com/6" },
    { id: 7, imageUrl: "data:image/png;base64,", companyName: "Company Seven", link: "https://example.com/7" },
    { id: 8, imageUrl: "data:image/jpeg;base64,", companyName: "Company Eight", link: "https://example.com/8" },
    { id: 9, imageUrl: "data:image/png;base64,", companyName: "Company Nine", link: "https://example.com/9" },
    { id: 10, imageUrl: "data:image/png;base64,", companyName: "Company Ten", link: "https://example.com/10" },
   
    // Fill the rest of the 100 cells as needed
  ];

  // Function to create a 10x10 grid with ad images
  const renderAdGrid = () => {
    const cells = [];
    
    // Create all cells
    for (let i = 0; i < 10; i++) {
      for (let j = 0; j < 10; j++) {
        const index = i * 10 + j;
        const ad = index < adData.length ? adData[index] : null;
        
        cells.push(
          <div 
            key={`cell-${i}-${j}`}
            className="relative border border-gray-400 overflow-hidden group cursor-pointer"
          >
            {ad && (
              <>
                <a href={ad.link} className="block w-full h-full">
                  <img 
                    src={ad.imageUrl} 
                    alt={ad.companyName}
                    className="w-full h-full object-cover"
                  />
                  <div className="absolute bottom-0 left-0 right-0 bg-black bg-opacity-70 text-white text-xs py-1 px-2 opacity-0 group-hover:opacity-100 transition-opacity duration-200">
                    {ad.companyName}
                  </div>
                </a>
              </>
            )}
          </div>
        );
      }
    }
    
    return cells;
  };
  
  return (
    <div className="w-full flex justify-center p-4">
      <div className="w-full max-w-screen-lg aspect-square">
        <div className="w-full h-full grid grid-cols-10 grid-rows-10 gap-0">
          {renderAdGrid()}
        </div>
      </div>
    </div>
  );
};

export default AdBanner;